<script> //Нужен для работы выпадающих списков
    const headers = document.querySelectorAll('.mb-5 header');

    function toggleList(event) {
        const header = event.currentTarget;
        const list = header.nextElementSibling;
        const arrow = header.querySelector('svg');

        list.classList.toggle('max-h-none');
        list.classList.toggle('opacity-100');
        list.classList.toggle('max-h-0');
        list.classList.toggle('opacity-0');

        arrow.classList.toggle('rotate-180');
    }

    headers.forEach(header => {
        header.addEventListener('click', toggleList);
    });
</script>
